        <button (click)="clickAddEmployee()" type="button" data-toggle="modal" data-target="#exampleModal" class="btn btn-success">Add Employee</button>
      </div>
    </div>
  </nav>
  <br>

  <div class="container">
  <table class="table table-bordered mt-3">
    <thead>
      <tr>
        <th scope="col">Employee ID</th>
        <th scope="col">First Name</th>
        <th scope="col">Last Name</th>
        <th scope="col">Email ID</th>
        <th scope="col">Mobile No.</th>
        <th scope="col">Salary</th>
        <th scope="col">Action</th>
      </tr>
    </thead>
    <!-- <tbody>
      <tr *ngFor= "let row of employeeData">
        <td>{{row.id}}</td>
        <td>{{row.firstName}}</td>
        <td>{{row.lastName}}</td>
        <td>{{row.email}}</td>
        <td>{{row.email}}</td>
        <td>₹{{row.salary}}</td>
        <td>
          <button (click)="OnEdit(row)" class="btn btn-info" type="button" data-toggle="modal" data-target="#exampleModal">Edit</button>
          <button (click)="deleteEmployee(row)" class="btn btn-danger mx-3">Delete</button>
        </td>
      </tr>
    </tbody> -->
    <tbody>
      <tr *ngFor= "let row of employeeData; let i=index">
        <td>{{i+1}}</td>
        <td>{{row.firstName}}</td>
        <td>{{row.lastName}}</td>
        <td>{{row.email}}</td>
        <td>{{row.mobile}}</td>
        <td>₹{{row.salary}}</td>
        <td>
          <button (click)="OnEdit(row)" class="btn btn-info" type="button" data-toggle="modal" data-target="#exampleModal">Edit</button>
          <!-- <button (click)="deleteEmployee(row)" class="btn btn-danger mx-3">Delete</button> -->
          <button type="button" data-toggle="modal" (click)="setID(row)" data-target="#confirmDelete" class="btn btn-danger mx-3">Delete</button>
        </td>
      </tr>
    </tbody>

  </table>
  <!-- For Confirming Delete -->
<div class="modal fade" id="confirmDelete" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title" id="exampleModalLabel">Are You Sure?</h5>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-secondary" data-dismiss="modal">No</button>
        <button type="button" class="btn btn-primary" (click)="deleteEmployee(row)">Yes</button>
      </div>
    </div>
  </div>
</div>
  </div>

  <!-- Modal -->
  <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
    <div class="modal-dialog" role="document">
      <div class="modal-content">
        <div class="modal-header"> 
          <h5 *ngIf="showAdd" class="modal-title" id="exampleModalLabel">Insert Details</h5>
          <h5 *ngIf="showUpdate" class="modal-title" id="exampleModalLabel">Update Details</h5>
          <button type="button" class="close" data-dismiss="modal" aria-label="Close">
            <span aria-hidden="true">&times;</span>
          </button>
        </div>
        <div class="modal-body">
            <form [formGroup]= "formValue">
                <div class="form-group">
                  <label for="exampleInputEmail1">First Name</label>
                  <input type="text" formControlName="firstName" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp">
                </div>
                <div class="form-group">
